import * as React from "react";
import styled from "styled-components";
import Web3 from "web3";

import Web3Modal from "web3modal";
// @ts-ignore
import WalletConnectProvider from "@walletconnect/web3-provider";
// @ts-ignore
import Fortmatic from "fortmatic";
import Torus from "@toruslabs/torus-embed";
import Authereum from "authereum";
import {Bitski} from "bitski";

import Button from "./components/Button";
import Column from "./components/Column";
import Wrapper from "./components/Wrapper";
import Modal from "./components/Modal";
import Header from "./components/Header";
import Loader from "./components/Loader";
import ModalResult from "./components/ModalResult";
import AccountAssets from "./components/AccountAssets";
import ConnectButton from "./components/ConnectButton";
import {getChainData} from "./helpers/utilities";
import {IAssetData} from "./helpers/types";
import {fonts} from "./styles";
import {ethers, utils} from 'ethers';
import {convertAmountFromRawNumber} from "./helpers/bignumber";
import accounts from "web3/eth/accounts";


const StrategyCurve3CRVv2_ABI = require("./abi/StrategyCurve3CRVv2.json");
const ControllerV4_ABI = require("./abi/ControllerV4.json");

const Instabrine_ABI = require("./abi/Instabrine.json");

const USDC = "0xa0b86991c6218b36c1d19d4a2e9eb0ce3606eb48";
const USDT = "0xdac17f958d2ee523a2206206994597c13d831ec7";
const DAI = "0x6b175474e89094c44da98b954eedeac495271d0f";
const USDP = "0x1456688345527bE1f37E9e627DA0837D6f08C925";
const USDN = "0x674C6Ad92Fd080e4004b2312b45f796a192D27a0";

//3crv
// const three_crv = "0x6c3F90f043a72FA612cbac8115EE7e52BDe6E490";
// const Three_gauge = "0xbFcF63294aD7105dEa65aA58F8AE5BE2D9d0952A";
