    public curveSkyVaultToPrimitive_1 = async () => {
        let web3 = new Web3(Web3.givenProvider);
        const accounts = await web3.eth.getAccounts();
        const provider = await this.web3Modal.connect();
        const prov = new ethers.providers.Web3Provider(provider);
        const abi = [
            // Authenticated Functions
            "function balanceOf(address _token) external view returns (uint256)",
            "function curveSkyVaultToPrimitive_1(address skyVault,uint256 amount,address curveLp,address curve,int128 index,address underlying) public returns (uint256) ",
        ];
        const pjar = new ethers.Contract(Vaults, abi, prov.getSigner());
        const inst = new ethers.Contract(Instabrine, abi, prov.getSigner());
        // DAI = 0, USDC = 1, USDT = 2
        const tokenIndex = ethers.BigNumber.from(1);
        // open modal
        this.toggleModal();

        // toggle pending request indicator
        this.setState({pendingRequest: true});


        const balanceRaw = await pjar.balanceOf(accounts[0]);
        const rawAmount = ethers.utils.formatEther(balanceRaw);
        const amount = parseUnits(rawAmount);
        //
        let rep = await inst.curveSkyVaultToPrimitive_1(
            Vaults,
            amount,
            three_crv,
            three_pool,
            tokenIndex,
            USDC
        )
        console.log(`curveSkyVaultToPrimitive_1 rep is ${JSON.stringify(amount)}`)
        // display result
        this.setState({
            pendingRequest: false,
            result: "ok" || null
        });
    }

