function initWeb3(provider) {
  const web3 = new Web3(provider);
  web3.eth.extend({
    methods: [{
      name: "chainId",
      call: "eth_chainId",
      outputFormatter: web3.utils.hexToNumber
    }]
  });
  return web3;
}

