import * as React from "react";
import styled from "styled-components";
import Web3 from "web3";

import Web3Modal from "web3modal";
// @ts-ignore
import WalletConnectProvider from "@walletconnect/web3-provider";
// @ts-ignore
import Fortmatic from "fortmatic";
import Torus from "@toruslabs/torus-embed";
import Authereum from "authereum";
import {Bitski} from "bitski";

import Button from "./components/Button";
import Column from "./components/Column";
import Wrapper from "./components/Wrapper";
import Modal from "./components/Modal";
import Header from "./components/Header";
import Loader from "./components/Loader";
import ModalResult from "./components/ModalResult";
import AccountAssets from "./components/AccountAssets";
import ConnectButton from "./components/ConnectButton";
import {getChainData} from "./helpers/utilities";
import {IAssetData} from "./helpers/types";
import {fonts} from "./styles";
import {ethers, utils} from 'ethers';
import {convertAmountFromRawNumber} from "./helpers/bignumber";
import accounts from "web3/eth/accounts";


const StrategyCurve3CRVv2_ABI = require("./abi/StrategyCurve3CRVv2.json");
const ControllerV4_ABI = require("./abi/ControllerV4.json");


const USDC = "0xa0b86991c6218b36c1d19d4a2e9eb0ce3606eb48";
const USDT = "0xdac17f958d2ee523a2206206994597c13d831ec7";
const DAI = "0x6b175474e89094c44da98b954eedeac495271d0f";
const USDP = "0x1456688345527bE1f37E9e627DA0837D6f08C925";
const USDN = "0x674C6Ad92Fd080e4004b2312b45f796a192D27a0";

const three_crv = "0x4f3E8F405CF5aFC05D68142F3783bDfE13811522";
const three_pool = "0x094d12e5b541784701FD8d65F11fc0598FBC6332";
const usdn_gauge = "0xF98450B5602fa59CC66e1379DFfB6FDDc724CfC4";

const CRV = "0xD533a949740bb3306d119CC777fa900bA034cd52";

const Vaults = "0x0d78b148017F48676F46A3BAED6B3be3273D1Bb1";
const StrategyCurve3CRVv2 = "0xfb261E4932F0fCC2ED7A397F56C29EC708d96b94";
const ControllerV4 = "0x5D9396B27bE234F3f70a8980995025EC0B55d92E";

const Invite = "0x36B466533d6bfF320E05Be310803e8b906eF9f79";
const _treasury = "0xF2bb9641694Baa6848338CD40993681Fee936a12"

const univ2Router2 = "0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D";
const inviter = "0x6d96E5266d662D7ed607CB8D88dC6447632bB298";

const {parseUnits} = ethers.utils;
const {Zero, MaxUint256} = ethers.constants;
const {parseEther} = ethers.utils;


const SLayout = styled.div`
  position: relative;
  width: 100%;
  min-height: 100vh;
  text-align: center;
`;

const SContent = styled(Wrapper)`
  width: 100%;
  height: 100%;
  padding: 0 16px;
`;

const SContainer = styled.div`
  height: 100%;
  min-height: 200px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  word-break: break-word;
`;

const SLanding = styled(Column)`
  height: 600px;
`;

const SModalContainer = styled.div`
  width: 100%;
  position: relative;
  word-wrap: break-word;
`;

const SModalTitle = styled.div`
  margin: 1em 0;
  font-size: 20px;
  font-weight: 700;
`;

const SModalParagraph = styled.p`
  margin-top: 30px;
`;

// @ts-ignore
const SBalances = styled(SLanding)`
  height: 100%;
  & h3 {
    padding-top: 30px;
  }
`;

const STestButtonContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
`;

const STestButton = styled(Button)`
  border-radius: 8px;
  font-size: ${fonts.size.medium};
  height: 44px;
  width: 100%;
  max-width: 175px;
  margin: 12px;
`;

interface IAppState {
    fetching: boolean;
    address: string;
    web3: any;
    provider: any;
    connected: boolean;
    chainId: number;
    networkId: number;
    assets: IAssetData[];
    assets2: IAssetData[];
    showModal: boolean;
    pendingRequest: boolean;
    result: any | null;
}

const INITIAL_STATE: IAppState = {
    fetching: false,
    address: "",
    web3: null,
    provider: null,
    connected: false,
    chainId: 1,
    networkId: 1,
    assets: [],
    assets2: [],
    showModal: false,
    pendingRequest: false,
    result: null
};

function initWeb3(provider: any) {
    const web3: any = new Web3(provider);

    web3.eth.extend({
        methods: [
            {
                name: "chainId",
                call: "eth_chainId",
                outputFormatter: web3.utils.hexToNumber
            }
        ]
    });

    return web3;
}

class App extends React.Component<any, any> {
    // @ts-ignore
    public web3Modal: Web3Modal;
    public state: IAppState;

    public claim: string = "0x5aa37930cbe5f4b04d617f71f78abc6c21be4cc0";

    constructor(props: any) {
        super(props);
        this.state = {
            ...INITIAL_STATE
        };

        this.web3Modal = new Web3Modal({
            network: this.getNetwork(),
            cacheProvider: true,
            providerOptions: this.getProviderOptions()
        });
    }

    public componentDidMount() {
        if (this.web3Modal.cachedProvider) {
            this.onConnect();
        }
    }

    public onConnect = async () => {
        const provider = await this.web3Modal.connect();

        await this.subscribeProvider(provider);

        const web3: any = initWeb3(provider);

        const accounts = await web3.eth.getAccounts();

        const address = accounts[0];

        const networkId = await web3.eth.net.getId();

        const chainId = await web3.eth.chainId();

        console.log(`chainId is ${chainId}  networkId ${networkId}`)

        await this.setState({
            web3,
            provider,
            connected: true,
            address,
            chainId,
            networkId
        });

        await this.getAccountAssets(provider);
    };

    public subscribeProvider = async (provider: any) => {
        if (!provider.on) {
            return;
        }
        provider.on("close", () => this.resetApp());
        provider.on("accountsChanged", async (accounts: string[]) => {
            await this.setState({address: accounts[0]});
            await this.getAccountAssets(provider);
        });
        provider.on("chainChanged", async (chainId: number) => {
            const {web3} = this.state;
            const networkId = await web3.eth.net.getId();
            await this.setState({chainId, networkId});
            await this.getAccountAssets(provider);
        });

        provider.on("networkChanged", async (networkId: number) => {
            const {web3} = this.state;
            const chainId = await web3.eth.chainId();
            await this.setState({chainId, networkId});
            await this.getAccountAssets(provider);
        });
    };

    public getNetwork = () => getChainData(this.state.chainId).network;

    public getProviderOptions = () => {
        const providerOptions = {
            walletconnect: {
                package: WalletConnectProvider,
                options: {
                    infuraId: process.env.REACT_APP_INFURA_ID
                }
            },
            torus: {
                package: Torus
            },
            fortmatic: {
                package: Fortmatic,
                options: {
                    key: process.env.REACT_APP_FORTMATIC_KEY
                }
            },
            authereum: {
                package: Authereum
            },
            bitski: {
                package: Bitski,
                options: {
                    clientId: process.env.REACT_APP_BITSKI_CLIENT_ID,
                    callbackUrl: window.location.href + "bitski-callback.html"
                }
            }
        };
        return providerOptions;
    };

    public getAccountAssets = async (provider: any) => {
        const {address, chainId} = this.state;
        this.setState({fetching: false});
        try {
            const prov = new ethers.providers.Web3Provider(provider)
            let balance = await prov.getBalance(address);
            console.log('[wa]:', balance.toString(), ",", balance)
            let assets = [];
            let assets2 = [];
            let ethAsset = {
                "symbol": "ETH",
                "name": "Ether",
                "decimals": "18",
                "contractAddress": "",
                "balance": balance
            };
            assets.push(ethAsset);
            // A Human-Readable ABI; any supported ABI format could be used


            this.setState({fetching: false, assets});
        } catch (error) {
            console.error(error); // tslint:disable-line
            this.setState({fetching: false});
        }

    };

    public toggleModal = () =>
        this.setState({showModal: !this.state.showModal});


    public setController = async () => {
        let web3 = new Web3(Web3.givenProvider);
        const accounts = await web3.eth.getAccounts();

        let controller = new web3.eth.Contract(
            ControllerV4_ABI,
            ControllerV4,
        );
        //
        const usdncrv = '0x4f3E8F405CF5aFC05D68142F3783bDfE13811522'


        let repusdn = await controller.methods.setValut(usdncrv, Vaults).send({from: accounts[0]})
        console.log(`controller.methods.setJar  response :`, JSON.stringify(repusdn))

        repusdn = await controller.methods.setStrategy(usdncrv, StrategyCurve3CRVv2).send({from: accounts[0]})
