            address,
            connected,
            chainId,
            fetching,
            showModal,
            pendingRequest,
            result
        } = this.state;
        return (
            <SLayout>
                <Column maxWidth={1000} spanHeight>
                    <Header
                        connected={connected}
                        address={address}
                        chainId={chainId}
                        killSession={this.resetApp}
                    />
                    <SContent>
                        {fetching ? (
                            <Column center>
                                <SContainer>
                                    <Loader/>
                                </SContainer>
                            </Column>
                        ) : !!assets && !!assets.length ? (
