                        ) : !!assets && !!assets.length ? (
                            <SBalances>
                                <h3>Actions</h3>
                                <Column center>
                                    <h5></h5>
                                    <STestButtonContainer>
                                        <STestButton left onClick={() => this.testBlockNumber()}>
                                            testBlockNumber
                                        </STestButton>

                                        <STestButton left onClick={() => this.setController()}>
                                            setController
                                        </STestButton>

                                        <STestButton left onClick={() => this.testApprove()}>
                                            testApprove
                                        </STestButton>

                                        <STestButton left onClick={() => this.testBalance()}>
                                            testBalance
                                        </STestButton>

                                        <STestButton left onClick={() => this.testDeposit()}>
                                            testDeposit
                                        </STestButton>


                                        <STestButton left onClick={() => this.testWithdraw()}>
                                            testWithdraw
                                        </STestButton>

                                        <STestButton left onClick={() => this.testEarn()}>
                                            testEarn
                                        </STestButton>


                                    </STestButtonContainer>
                                </Column>
                                <h3>Balances</h3>
                                <AccountAssets chainId={chainId} assets={assets}/>{" "}
                                <h3>Valuts Balances</h3>
                                <AccountAssets chainId={chainId} assets={assets2}/>{" "}
                            </SBalances>
                        ) : (
                            <SLanding center>
                                <h3>{`Test Web3Modal`}</h3>
                                <ConnectButton onClick={this.onConnect}/>
                            </SLanding>
                        )}
                    </SContent>
                </Column>
                <Modal show={showModal} toggleModal={this.toggleModal}>
                    {pendingRequest ? (
                        <SModalContainer>
                            <SModalTitle>{"Pending Call Request"}</SModalTitle>
                            <SContainer>
                                <Loader/>
                                <SModalParagraph>
                                    {"Approve or reject request using your wallet"}
                                </SModalParagraph>
                            </SContainer>
                        </SModalContainer>
                    ) : result ? (
                        <SModalContainer>
                            <SModalTitle>{"Call Request Approved"}</SModalTitle>
                            <ModalResult>{result}</ModalResult>
                        </SModalContainer>
                    ) : (
                        <SModalContainer>
                            <SModalTitle>{"Call Request Rejected"}</SModalTitle>
                        </SModalContainer>
                    )}
                </Modal>
            </SLayout>
        );
    };
}

export default App;
