import * as React from "react";
import { MaticPOSClient } from '@maticnetwork/maticjs'
import HDWalletProvider from '@truffle/hdwallet-provider'
import styled from "styled-components";
import Button from "./components/Button";
import Column from "./components/Column";
import Wrapper from "./components/Wrapper";

import Web3 from "web3";

import Web3Modal from "web3modal";
// @ts-ignore
import WalletConnectProvider from "@walletconnect/web3-provider";
// @ts-ignore
import Fortmatic from "fortmatic";
import Torus from "@toruslabs/torus-embed";
import Authereum from "authereum";
import {Bitski} from "bitski";

import Modal from "./components/Modal";
import Header from "./components/Header";
import Loader from "./components/Loader";
import ModalResult from "./components/ModalResult";
import AccountAssets from "./components/AccountAssets";
import ConnectButton from "./components/ConnectButton";
import { getChainData } from "./helpers/utilities";
import { IAssetData } from "./helpers/types";
import { fonts } from "./styles";
import { ethers, utils } from 'ethers';



const SLayout = styled.div`
  position: relative;
  width: 100%;
  min-height: 100vh;
  text-align: center;
`;

const SContent = styled(Wrapper)`
  width: 100%;
  height: 100%;
  padding: 0 16px;
`;

const SContainer = styled.div`
  height: 100%;
  min-height: 200px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  word-break: break-word;
`;

const SLanding = styled(Column)`
  height: 600px;
`;

const SModalContainer = styled.div`
  width: 100%;
  position: relative;
  word-wrap: break-word;
`;

const SModalTitle = styled.div`
  margin: 1em 0;
  font-size: 20px;
  font-weight: 700;
`;

const SModalParagraph = styled.p`
  margin-top: 30px;
`;

// @ts-ignore
const SBalances = styled(SLanding)`
  height: 100%;
  & h3 {
    padding-top: 30px;
  }
`;

const STestButtonContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
`;

const STestButton = styled(Button)`
  border-radius: 8px;
  font-size: ${fonts.size.medium};
  height: 44px;
  width: 100%;
  max-width: 175px;
  margin: 12px;
`;

const STestInput = styled.textarea`
    background-color: #f9f9f9;
    border: 0;
    border-radius: 4px;
    height: 200px;
    padding: 13px 15px;
    resize: none;
    width: 100%;
`;

interface IAppState {
    fetching: boolean;
    address: string;
    web3: any;
    provider: any;
    connected: boolean;
    chainId: number;
    networkId: number;
    assets: IAssetData[];
    assets2: IAssetData[];
    showModal: boolean;
    pendingRequest: boolean;
    result: any | null;
    message: string;
    prvkey: string;
    curTime: any;
}

const INITIAL_STATE: IAppState = {
    fetching: false,
    address: "",
    web3: null,
    provider: null,
    connected: false,
    chainId: 1,
    networkId: 1,
    assets: [],
    assets2: [],
    showModal: false,
    pendingRequest: false,
    result: null,
    message: "",
    prvkey:"",
    curTime: null,
};

function initWeb3(provider: any) {
    const web3: any = new Web3(provider);

    web3.eth.extend({
        methods: [
            {
                name: "chainId",
                call: "eth_chainId",
                outputFormatter: web3.utils.hexToNumber
            }
        ]
    });

    return web3;
}


class App extends React.Component<any, any> {
    // @ts-ignore
    public web3Modal: Web3Modal;
    public state: IAppState;

    constructor(props: any) {
        super(props);
        this.state = {
            ...INITIAL_STATE
        };

        this.web3Modal = new Web3Modal({
            network: this.getNetwork(),
            cacheProvider: true,
            providerOptions: this.getProviderOptions()
        });
        setInterval( () => { this.setState({curTime: new  Date().toUTCString()});}, 1000);
    }

    public componentDidMount() {
        if (this.web3Modal.cachedProvider) {
            this.onConnect();
        }
    }

    public onConnect = async () => {
        const provider = await this.web3Modal.connect();

        await this.subscribeProvider(provider);

        const web3: any = initWeb3(provider);

        const accounts = await web3.eth.getAccounts();

        const address = accounts[0];

        const networkId = await web3.eth.net.getId();

        const chainId = await web3.eth.chainId();

        console.log(`chainId is ${chainId}  networkId ${networkId}`)

        await this.setState({
            web3,
            provider,
            connected: true,
            address,
            chainId,
            networkId
        });


    };

    public subscribeProvider = async (provider: any) => {
        if (!provider.on) {
            return;
        }
        provider.on("close", () => this.resetApp());
        provider.on("accountsChanged", async (accounts: string[]) => {
            await this.setState({address: accounts[0]});

        });
        provider.on("chainChanged", async (chainId: number) => {
            const {web3} = this.state;
            const networkId = await web3.eth.net.getId();
            await this.setState({chainId, networkId});

        });

        provider.on("networkChanged", async (networkId: number) => {
            const {web3} = this.state;
            const chainId = await web3.eth.chainId();
            await this.setState({chainId, networkId});

        });
    };

    public getNetwork = () => getChainData(this.state.chainId).network;

    public getProviderOptions = () => {
        const providerOptions = {
            walletconnect: {
                package: WalletConnectProvider,
                options: {
                    infuraId: process.env.REACT_APP_INFURA_ID
                }
            },
            torus: {
                package: Torus
            },
            fortmatic: {
                package: Fortmatic,
                options: {
                    key: process.env.REACT_APP_FORTMATIC_KEY
                }
            },
            authereum: {
                package: Authereum
            },
            bitski: {
                package: Bitski,
                options: {
                    clientId: process.env.REACT_APP_BITSKI_CLIENT_ID,
                    callbackUrl: window.location.href + "bitski-callback.html"
                }
            }
        };
        return providerOptions;
    };

    public resetApp = async () => {
        const {web3} = this.state;
        if (web3 && web3.currentProvider && web3.currentProvider.close) {
            await web3.currentProvider.close();
        }
        await this.web3Modal.clearCachedProvider();
        this.setState({...INITIAL_STATE});
    };

    public toggleModal = () =>
        this.setState({showModal: !this.state.showModal});

    public setmessage = (value: any) => {
        this.setState({message: value.target.value})
    }

    public generateExit = async () => {
        const provider = await this.web3Modal.connect();
        const prov = new ethers.providers.Web3Provider(provider);

        const maticPOSClient = new MaticPOSClient({
            // optional parameter, defaults to 'testnet'
            // use 'mainnet' for mainnet deployment
            network: 'mainnet',

            // optional parameter, defaults to mumbai
            // use 'v1' for mainnet deployment
            version: 'v1',

            // Set Matic chain provider instance
            // Example:
            // new HDWalletProvider(<USER_PRIVATE_KEY>, 'https://rpc-mumbai.matic.today')
            maticProvider: new HDWalletProvider('0x1b15aab3cf6835c12f1571243617e9482201d6ae0441fb8b10678940c061d482', 'https://rpc-mainnet.maticvigil.com/v1/03b78b6c77886982589984e4c4e40d81a5f5b47e'),

            // Set Main chain provider instance
            // Example: new HDWalletProvider(<USER_PRIVATE_KEY>, <GOERLI_RPC_URL>)
            parentProvider:  new HDWalletProvider('0x1b15aab3cf6835c12f1571243617e9482201d6ae0441fb8b10678940c061d482', 'https://mainnet.infura.io/v3/c76e265d230c44e4ae19f69ecb6a1ff4'),


            // Set RootChainManager address of PoS Portal contracts.
