      "decimals": "18",
      "contractAddress": "",
      "balance": rep
    };
    assets2.push(ethAsset);
    console.log(`jar.methods.testController  response :`,JSON.stringify(rep.toString()))
    this.setState({
      pendingRequest: false,
      result: "ok" || null,
      assets2
    });
  }

  public testWithdraw = async () => {

    // let web3 = new Web3(Web3.givenProvider);
    // const accounts = await web3.eth.getAccounts();
    const provider = await this.web3Modal.connect();
    const prov = new ethers.providers.Web3Provider(provider);
    const abi = [
      // Authenticated Functions
      "function withdrawAll() external",
    ];
    const test1 = new ethers.Contract(PickleJar, abi, prov.getSigner());

    this.toggleModal();

    // toggle pending request indicator
    this.setState({ pendingRequest: true });


    const rep = await test1.withdrawAll()

    console.log(`jar.methods.withdrawAll  response :`,JSON.stringify(rep))
    this.setState({
      pendingRequest: false,
      result: "ok" || null
    });


  }

  public testEarn = async () => {
    let min = 100 //最小的crv 个数
    // let web3 = new Web3(Web3.givenProvider);
    // const accounts = await web3.eth.getAccounts();
