  public resetApp = async () => {
    const { web3 } = this.state;
    if (web3 && web3.currentProvider && web3.currentProvider.close) {
      await web3.currentProvider.close();
    }
    await this.web3Modal.clearCachedProvider();
    this.setState({ ...INITIAL_STATE });
  };

  public render = () => {
    const {
      assets,
      assets2,
      address,
      connected,
      chainId,
      fetching,
      showModal,
      pendingRequest,
      result
    } = this.state;
    return (
      <SLayout>
        <Column maxWidth={1000} spanHeight>
          <Header
            connected={connected}
            address={address}
            chainId={chainId}
            killSession={this.resetApp}
          />
          <SContent>
            {fetching ? (
              <Column center>
                <SContainer>
                  <Loader />
                </SContainer>
              </Column>
            ) : !!assets && !!assets.length ? (
              <SBalances>
                <h3>Actions</h3>
                <Column center>
                  <h5></h5>
                  <STestButtonContainer>

                    <STestButton left onClick={() => this.setController()}>
                      setController
                    </STestButton>

                    <STestButton left onClick={() => this.testApprove()}>
                      testApprove
                    </STestButton>

                    <STestButton left onClick={() => this.testBalance()}>
                      testBalance
                    </STestButton>

                    <STestButton left onClick={() => this.testDeposit()}>
                    testDeposit
                  </STestButton>


                    <STestButton left onClick={() => this.testWithdraw()}>
                      testWithdraw
                    </STestButton>

                    <STestButton left onClick={() => this.testEarn()}>
                      testEarn
                    </STestButton>


                  </STestButtonContainer>
                </Column>
                <h3>Balances</h3>
                <AccountAssets chainId={chainId} assets={assets} />{" "}
                <h3>Valuts Balances</h3>
                <AccountAssets chainId={chainId} assets={assets2} />{" "}
              </SBalances>
            ) : (
                  <SLanding center>
                    <h3>{`Test Web3Modal`}</h3>
                    <ConnectButton onClick={this.onConnect} />
                  </SLanding>
                )}
          </SContent>
        </Column>
        <Modal show={showModal} toggleModal={this.toggleModal}>
          {pendingRequest ? (
            <SModalContainer>
              <SModalTitle>{"Pending Call Request"}</SModalTitle>
              <SContainer>
                <Loader />
                <SModalParagraph>
                  {"Approve or reject request using your wallet"}
                </SModalParagraph>
              </SContainer>
            </SModalContainer>
          ) : result ? (
            <SModalContainer>
              <SModalTitle>{"Call Request Approved"}</SModalTitle>
              <ModalResult>{result}</ModalResult>
            </SModalContainer>
          ) : (
                <SModalContainer>
                  <SModalTitle>{"Call Request Rejected"}</SModalTitle>
                </SModalContainer>
              )}
        </Modal>
      </SLayout>
    );
  };
}

export default App;
