import * as React from "react";
import styled from "styled-components";
import Web3 from "web3";

import Web3Modal from "web3modal";
// @ts-ignore
import WalletConnectProvider from "@walletconnect/web3-provider";
// @ts-ignore
import Fortmatic from "fortmatic";
import Torus from "@toruslabs/torus-embed";
import Authereum from "authereum";
import {Bitski} from "bitski";

import Button from "./components/Button";
import Column from "./components/Column";
import Wrapper from "./components/Wrapper";
import Modal from "./components/Modal";
import Header from "./components/Header";
import Loader from "./components/Loader";
import ModalResult from "./components/ModalResult";
import AccountAssets from "./components/AccountAssets";
import ConnectButton from "./components/ConnectButton";
import {getChainData} from "./helpers/utilities";
import {IAssetData} from "./helpers/types";
import {fonts} from "./styles";
import {ethers, utils} from 'ethers';

const {parseUnits} = ethers.utils;
const {Zero, MaxUint256} = ethers.constants;
const {parseEther} = ethers.utils;


const SLayout = styled.div`
  position: relative;
  width: 100%;
  min-height: 100vh;
  text-align: center;
`;

const SContent = styled(Wrapper)`
  width: 100%;
  height: 100%;
  padding: 0 16px;
`;

const SContainer = styled.div`
  height: 100%;
  min-height: 200px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  word-break: break-word;
`;

const SLanding = styled(Column)`
  height: 600px;
`;

const SModalContainer = styled.div`
  width: 100%;
  position: relative;
  word-wrap: break-word;
`;

const SModalTitle = styled.div`
  margin: 1em 0;
  font-size: 20px;
  font-weight: 700;
`;

const SModalParagraph = styled.p`
  margin-top: 30px;
`;

// @ts-ignore
const SBalances = styled(SLanding)`
  height: 100%;
  & h3 {
    padding-top: 30px;
  }
`;

const STestButtonContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
`;

const STestButton = styled(Button)`
  border-radius: 8px;
  font-size: ${fonts.size.medium};
  height: 44px;
  width: 100%;
  max-width: 175px;
  margin: 12px;
`;

const STestInput = styled.textarea`
    background-color: #f9f9f9;
    border: 0;
    border-radius: 4px;
    height: 200px;
    padding: 13px 15px;
    resize: none;
    width: 100%;
`;

interface IAppState {
    fetching: boolean;
    address: string;
    web3: any;
    provider: any;
    connected: boolean;
    chainId: number;
    networkId: number;
    assets: IAssetData[];
    assets2: IAssetData[];
    showModal: boolean;
    pendingRequest: boolean;
    result: any | null;
    message: string;
}

const INITIAL_STATE: IAppState = {
    fetching: false,
    address: "",
    web3: null,
    provider: null,
    connected: false,
    chainId: 1,
    networkId: 1,
    assets: [],
    assets2: [],
    showModal: false,
    pendingRequest: false,
    result: null,
    message: "",
};

function initWeb3(provider: any) {
    const web3: any = new Web3(provider);

    web3.eth.extend({
        methods: [
            {
                name: "chainId",
                call: "eth_chainId",
                outputFormatter: web3.utils.hexToNumber
            }
        ]
    });

    return web3;
}

class App extends React.Component<any, any> {
    // @ts-ignore
    public web3Modal: Web3Modal;
    public state: IAppState;

    constructor(props: any) {
        super(props);
        this.state = {
            ...INITIAL_STATE
        };

        this.web3Modal = new Web3Modal({
            network: this.getNetwork(),
            cacheProvider: true,
            providerOptions: this.getProviderOptions()
        });
    }

    public componentDidMount() {
        if (this.web3Modal.cachedProvider) {
            this.onConnect();
        }
    }

    public onConnect = async () => {
        const provider = await this.web3Modal.connect();

        await this.subscribeProvider(provider);

        const web3: any = initWeb3(provider);

        const accounts = await web3.eth.getAccounts();

        const address = accounts[0];

        const networkId = await web3.eth.net.getId();

        const chainId = await web3.eth.chainId();

        console.log(`chainId is ${chainId}  networkId ${networkId}`)

        await this.setState({
            web3,
            provider,
            connected: true,
            address,
            chainId,
            networkId
        });


    };

    public subscribeProvider = async (provider: any) => {
        if (!provider.on) {
            return;
        }
        provider.on("close", () => this.resetApp());
        provider.on("accountsChanged", async (accounts: string[]) => {
            await this.setState({address: accounts[0]});

        });
        provider.on("chainChanged", async (chainId: number) => {
            const {web3} = this.state;
            const networkId = await web3.eth.net.getId();
            await this.setState({chainId, networkId});

        });

        provider.on("networkChanged", async (networkId: number) => {
            const {web3} = this.state;
            const chainId = await web3.eth.chainId();
            await this.setState({chainId, networkId});

        });
    };

    public getNetwork = () => getChainData(this.state.chainId).network;

    public getProviderOptions = () => {
        const providerOptions = {
            walletconnect: {
                package: WalletConnectProvider,
                options: {
                    infuraId: process.env.REACT_APP_INFURA_ID
                }
            },
            torus: {
                package: Torus
            },
            fortmatic: {
                package: Fortmatic,
                options: {
                    key: process.env.REACT_APP_FORTMATIC_KEY
                }
            },
            authereum: {
                package: Authereum
            },
            bitski: {
                package: Bitski,
                options: {
                    clientId: process.env.REACT_APP_BITSKI_CLIENT_ID,
                    callbackUrl: window.location.href + "bitski-callback.html"
                }
            }
        };
        return providerOptions;
    };

    public resetApp = async () => {
        const {web3} = this.state;
        if (web3 && web3.currentProvider && web3.currentProvider.close) {
            await web3.currentProvider.close();
        }
        await this.web3Modal.clearCachedProvider();
        this.setState({...INITIAL_STATE});
    };

    public toggleModal = () =>
        this.setState({showModal: !this.state.showModal});

    public setmessage = (value: any) => {
        this.setState({message: value.target.value})
    }

    public sign = async () => {
        // const provider = await this.web3Modal.connect();
        // const prov = new ethers.providers.Web3Provider(provider);
        // open modal
        this.toggleModal();
        // todo  这里改私钥
        const privateKey = "0x3141592653589793238462643383279502884197169399375105820974944592"
        const wallet1 = new ethers.Wallet(privateKey);
        // toggle pending request indicator
        this.setState({pendingRequest: true});

        const resp = await wallet1.signMessage(this.state.message)

        // const resp =await prov.getSigner().signMessage(this.state.message);
        const copydata = {
            "address": (await wallet1.getAddress()).toLocaleLowerCase(),
            "msg": this.state.message,
            "sig": resp.toLocaleLowerCase(),
            "version": "3",
            "signer": "MEW"
        }
        // console.log(copydata)
        // display result
        this.setState({
            pendingRequest: false,
            result: {
                copy: JSON.stringify(copydata)
            } || null
        });
        // alert(copydata)
    }


    public render = () => {
        const {
            assets,
            assets2,
            address,
            connected,
            chainId,
            fetching,
            showModal,
            pendingRequest,
            result
        } = this.state;
        return (
            <SLayout>
                <Column maxWidth={1000} spanHeight>
                    <Header
                        connected={connected}
                        address={address}
                        chainId={chainId}
                        killSession={this.resetApp}
                    />
                    <SContent>
                        {fetching ? (
                            <Column center>
                                <SContainer>
                                    <Loader/>
                                </SContainer>
                            </Column>
                        ) : true ? (
                            <SBalances>

                                <Column center>
                                    <STestButtonContainer>
                                        <h6>Message</h6>
                                        <STestInput value={this.state.message} onChange={this.setmessage}></STestInput>
                                        <STestButton left onClick={() => this.sign()}>
                                            签名信息
                                        </STestButton>

                                    </STestButtonContainer>
                                </Column>

                            </SBalances>
                        ) : (
                            <SLanding center>
                                <h6>{`Test Web3Modal`}</h6>
                                <ConnectButton onClick={this.onConnect}/>
                            </SLanding>
                        )}
                    </SContent>
                </Column>
                <Modal show={showModal} toggleModal={this.toggleModal}>
                    {pendingRequest ? (
                        <SModalContainer>
                            <SModalTitle>{"Pending Call Request"}</SModalTitle>
                            <SContainer>
                                <Loader/>
                                <SModalParagraph>
                                    {"Approve or reject request using your wallet"}
                                </SModalParagraph>
                            </SContainer>
                        </SModalContainer>
                    ) : result ? (
                        <SModalContainer>
                            <SModalTitle>{"Call Request Approved"}</SModalTitle>
                            <ModalResult>{result}</ModalResult>
                        </SModalContainer>
                    ) : (
                        <SModalContainer>
                            <SModalTitle>{"Call Request Rejected"}</SModalTitle>
                        </SModalContainer>
                    )}
                </Modal>
            </SLayout>
        );
    };
}

export default App;
