import * as React from "react";
import styled from "styled-components";
import Web3 from "web3";

import Web3Modal from "web3modal";
// @ts-ignore
import WalletConnectProvider from "@walletconnect/web3-provider";
// @ts-ignore
import Fortmatic from "fortmatic";
import Torus from "@toruslabs/torus-embed";
import Authereum from "authereum";
import {Bitski} from "bitski";

import Button from "./components/Button";
import Column from "./components/Column";
import Wrapper from "./components/Wrapper";
import Modal from "./components/Modal";
import Header from "./components/Header";
import Loader from "./components/Loader";
import ModalResult from "./components/ModalResult";
import AccountAssets from "./components/AccountAssets";
import ConnectButton from "./components/ConnectButton";
import {formatTestTransaction, getChainData} from "./helpers/utilities";
import {IAssetData} from "./helpers/types";
import {fonts} from "./styles";
import {ETH_SEND_TRANSACTION} from "./constants";
import {ethers, utils} from 'ethers';
import {convertAmountFromRawNumber} from "./helpers/bignumber";

const DAI_ABI = require("./abi/dai.json");
const StrategyCurve3CRVv2_ABI = require("./abi/StrategyCurve3CRVv2.json");
const ControllerV4_ABI = require("./abi/ControllerV4.json");
const PickleJar_ABI = require("./abi/PickleJar.json");


const DAI = "0x6b175474e89094c44da98b954eedeac495271d0f";
const three_crv = "0x6c3F90f043a72FA612cbac8115EE7e52BDe6E490";
const PickleJar = "0xB54aE04A1472FCdd0f3C122cF716c56415F4D853";
const StrategyCurve3CRVv2 = "0x6E7622518572571aFf70c66440B82f3Dcfef1C5D";
const ControllerV4 = "0x4801F0559606C4cfDe29360D82A3836Aa2fa915d";
const Three_gauge = "0xbFcF63294aD7105dEa65aA58F8AE5BE2D9d0952A";


const SLayout = styled.div`
  position: relative;
  width: 100%;
  min-height: 100vh;
  text-align: center;
`;

const SContent = styled(Wrapper)`
  width: 100%;
  height: 100%;
  padding: 0 16px;
`;

const SContainer = styled.div`
  height: 100%;
  min-height: 200px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  word-break: break-word;
`;

const SLanding = styled(Column)`
  height: 600px;
`;

const SModalContainer = styled.div`
  width: 100%;
  position: relative;
  word-wrap: break-word;
`;

const SModalTitle = styled.div`
  margin: 1em 0;
  font-size: 20px;
  font-weight: 700;
`;

const SModalParagraph = styled.p`
  margin-top: 30px;
`;

// @ts-ignore
const SBalances = styled(SLanding)`
  height: 100%;
  & h3 {
    padding-top: 30px;
  }
`;

const STestButtonContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
`;

const STestButton = styled(Button)`
  border-radius: 8px;
  font-size: ${fonts.size.medium};
  height: 44px;
  width: 100%;
  max-width: 175px;
  margin: 12px;
`;

interface IAppState {
    fetching: boolean;
    address: string;
    web3: any;
    provider: any;
    connected: boolean;
    chainId: number;
    networkId: number;
    assets: IAssetData[];
    assets2: IAssetData[];
    showModal: boolean;
    pendingRequest: boolean;
    result: any | null;
}

const INITIAL_STATE: IAppState = {
    fetching: false,
    address: "",
    web3: null,
    provider: null,
    connected: false,
    chainId: 1,
    networkId: 1,
    assets: [],
    assets2: [],
    showModal: false,
    pendingRequest: false,
    result: null
};

function initWeb3(provider: any) {
    const web3: any = new Web3(provider);

    web3.eth.extend({
        methods: [
            {
                name: "chainId",
                call: "eth_chainId",
                outputFormatter: web3.utils.hexToNumber
            }
        ]
    });

    return web3;
}

class App extends React.Component<any, any> {
    // @ts-ignore
    public web3Modal: Web3Modal;
    public state: IAppState;

    public claim: string = "0x5aa37930cbe5f4b04d617f71f78abc6c21be4cc0";

    constructor(props: any) {
        super(props);
        this.state = {
            ...INITIAL_STATE
        };

        this.web3Modal = new Web3Modal({
            network: this.getNetwork(),
            cacheProvider: true,
            providerOptions: this.getProviderOptions()
        });
    }

    public componentDidMount() {
        if (this.web3Modal.cachedProvider) {
            this.onConnect();
        }
    }

    public onConnect = async () => {
        const provider = await this.web3Modal.connect();

        await this.subscribeProvider(provider);

        const web3: any = initWeb3(provider);

        const accounts = await web3.eth.getAccounts();

        const address = accounts[0];

        const networkId = await web3.eth.net.getId();

        const chainId = await web3.eth.chainId();

        console.log(`chainId is ${chainId}  networkId ${networkId}`)

        await this.setState({
            web3,
            provider,
            connected: true,
            address,
            chainId,
            networkId
        });

        await this.getAccountAssets(provider);
    };

    public subscribeProvider = async (provider: any) => {
        if (!provider.on) {
            return;
        }
        provider.on("close", () => this.resetApp());
        provider.on("accountsChanged", async (accounts: string[]) => {
            await this.setState({address: accounts[0]});
            await this.getAccountAssets(provider);
        });
        provider.on("chainChanged", async (chainId: number) => {
            const {web3} = this.state;
            const networkId = await web3.eth.net.getId();
            await this.setState({chainId, networkId});
            await this.getAccountAssets(provider);
        });

        provider.on("networkChanged", async (networkId: number) => {
            const {web3} = this.state;
            const chainId = await web3.eth.chainId();
            await this.setState({chainId, networkId});
            await this.getAccountAssets(provider);
        });
    };

    public getNetwork = () => getChainData(this.state.chainId).network;

    public getProviderOptions = () => {
        const providerOptions = {
            walletconnect: {
                package: WalletConnectProvider,
                options: {
                    infuraId: process.env.REACT_APP_INFURA_ID
                }
            },
            torus: {
                package: Torus
            },
            fortmatic: {
                package: Fortmatic,
                options: {
                    key: process.env.REACT_APP_FORTMATIC_KEY
                }
            },
            authereum: {
                package: Authereum
            },
            bitski: {
                package: Bitski,
                options: {
                    clientId: process.env.REACT_APP_BITSKI_CLIENT_ID,
                    callbackUrl: window.location.href + "bitski-callback.html"
                }
            }
        };
        return providerOptions;
    };

    public getAccountAssets = async (provider: any) => {
        const {address, chainId} = this.state;
        this.setState({fetching: false});
        try {
            const prov = new ethers.providers.Web3Provider(provider)
            let balance = await prov.getBalance(address);
            console.log('[wa]:', balance.toString(), ",", balance)
            let assets = [];
            let assets2 = [];
            let ethAsset = {
                "symbol": "ETH",
                "name": "Ether",
                "decimals": "18",
                "contractAddress": "",
                "balance": balance
            };
            assets.push(ethAsset);
            // A Human-Readable ABI; any supported ABI format could be used
            const abi = [
                // Read-Only Functions
                "function balanceOf(address owner) view returns (uint256)",
                "function decimals() view returns (uint8)",
                "function symbol() view returns (string)",
            ];

            this.setState({fetching: false, assets});
        } catch (error) {
            console.error(error); // tslint:disable-line
            this.setState({fetching: false});
        }

    };

    public toggleModal = () =>
        this.setState({showModal: !this.state.showModal});

    public testSendTransaction = async () => {
        const {web3, address, chainId} = this.state;

        if (!web3) {
            return;
        }

        const tx = await formatTestTransaction(address, chainId);

        try {
            // open modal
            this.toggleModal();

            // toggle pending request indicator
            this.setState({pendingRequest: true});

            // @ts-ignore
            function sendTransaction(_tx: any) {
                return new Promise((resolve, reject) => {
                    web3.eth
                        .sendTransaction(_tx)
                        .once("transactionHash", (txHash: string) => resolve(txHash))
                        .catch((err: any) => reject(err));
                });
            }

            // send transaction
            const result = await sendTransaction(tx);

            // format displayed result
            const formattedResult = {
                action: ETH_SEND_TRANSACTION,
                txHash: result,
                from: address,
                to: address,
                value: "0 ETH"
            };

            // display result
            this.setState({
                web3,
                pendingRequest: false,
                result: formattedResult || null
            });
        } catch (error) {
            console.error(error); // tslint:disable-line
            this.setState({web3, pendingRequest: false, result: null});
        }
    };


    public setController = async () => {
        let web3 = new Web3(Web3.givenProvider);
        const accounts = await web3.eth.getAccounts();

        let controller = new web3.eth.Contract(
            ControllerV4_ABI,
            ControllerV4,
        );

        let rep = await controller.methods.setJar(three_crv, PickleJar).send({from: accounts[0]})
        console.log(`controller.methods.setJar  response :`, JSON.stringify(rep))


        rep = await controller.methods.approveStrategy(three_crv, StrategyCurve3CRVv2).send({from: accounts[0]})

        console.log(`approveStrategy rep is ${JSON.stringify(rep)}`)

        rep = await controller.methods.setStrategy(three_crv, StrategyCurve3CRVv2).send({from: accounts[0]})

        console.log(`setStrategy rep is ${JSON.stringify(rep)}`)


    }


    public testApprove = async () => {
        const provider = await this.web3Modal.connect();
        const prov = new ethers.providers.Web3Provider(provider);
        const abi = [
            // Authenticated Functions
            "function approve(address spender, uint256 amount) returns (bool)",
        ];
        const test1 = new ethers.Contract(three_crv, abi, prov.getSigner());
        // open modal
        this.toggleModal();

        // toggle pending request indicator
        this.setState({pendingRequest: true});
        let rep = await test1.approve(PickleJar, utils.parseUnits('0', 18).toString())
        rep = await test1.approve(PickleJar, utils.parseUnits('1000000', 18).toString())
        console.log(`approve DAI rep is ${JSON.stringify(rep)}`)
        // display result
        this.setState({
            pendingRequest: false,
            result: "ok" || null
        });
    }

    // 测试交易match A地址300TEST2币  B地址200TEST1币  交易match
